CREATE OR REPLACE FUNCTION obter_status_radioterapia (nr_seq_fase_p rxt_campo.nr_seq_fase%type) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w rxt_tumor.nr_sequencia%type;
ds_retorno_w    varchar(20);


BEGIN

if (nr_seq_fase_p IS NOT NULL AND nr_seq_fase_p::text <> '') then
	select	max(d.nr_sequencia)
	into STRICT    nr_sequencia_w
	from    rxt_fase_tratamento b,
		rxt_tratamento c,
		rxt_tumor d
	where	nr_seq_fase_p       = b.nr_sequencia
	and     b.nr_seq_tratamento = c.nr_sequencia
	and     c.nr_seq_tumor      = d.nr_sequencia;

ds_retorno_w := get_treatment_status(nr_sequencia_w);

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
