CREATE OR REPLACE FUNCTION obter_status_radio_agenda (nr_sequencia_p rxt_tratamento.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_status_agenda_w rxt_agenda.ie_status_agenda%type;
qt_count_w      integer;
ds_retorno_w    varchar(20):= 'N';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	count(*)
	into STRICT    qt_count_w
	from    rxt_tratamento a,
		rxt_agenda b
	where	nr_sequencia_p = a.nr_sequencia
	and     a.nr_sequencia = b.nr_seq_tratamento
	and     ie_status_agenda = 'AP';

if (qt_count_w > 0) then
    ds_retorno_w := 'S';
end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
