CREATE OR REPLACE FUNCTION obter_status_recusa_pac (dt_administracao_p timestamp, dt_suspensao_p timestamp) RETURNS varchar AS $body$
DECLARE

ie_status_w     varchar(1);


BEGIN

if (dt_administracao_p IS NOT NULL AND dt_administracao_p::text <> '') and (coalesce(dt_suspensao_p::text, '') = '') then
        ie_status_w     := 'C';
elsif (coalesce(dt_administracao_p::text, '') = '') and (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then
        ie_status_w     := 'I';
elsif (coalesce(dt_administracao_p::text, '') = '') and (coalesce(dt_suspensao_p::text, '') = '') then
        ie_status_w     := 'J';
end if;


return  ie_status_w;

end;
