SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_reg_equipe (nr_seq_equipe_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(100) := wheb_mensagem_pck.get_texto(309490); -- Liberada
ie_status_liberada_w		bigint;
ie_status_not_liberada_w	bigint;
nr_sequencia_w			bigint;
ie_situacao_w			varchar(2);
/*J9 a J12*/

dt_partida_w		timestamp;
dt_chegada_local_w	timestamp;
dt_saida_local_w	timestamp;
dt_chegada_base_w	timestamp;
nr_seq_motivo_parada_w	bigint;
ie_almoco_w		varchar(2);


BEGIN


select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	eme_reg_resposta a
where	nr_seq_equipe = nr_seq_equipe_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	ie_status_liberada_w
from	eme_reg_resposta a
where	nr_seq_equipe = nr_seq_equipe_p
and    ((coalesce(dt_partida::text, '') = '' and coalesce(dt_chegada_local::text, '') = '' and coalesce(dt_saida_local::text, '') = '' and coalesce(dt_chegada_base::text, '') = '') or ((dt_partida IS NOT NULL AND dt_partida::text <> '') and (dt_chegada_local IS NOT NULL AND dt_chegada_local::text <> '') and (dt_saida_local IS NOT NULL AND dt_saida_local::text <> '') and coalesce(dt_chegada_base::text, '') = '') or ((dt_partida IS NOT NULL AND dt_partida::text <> '') and (dt_chegada_local IS NOT NULL AND dt_chegada_local::text <> '') and (dt_saida_local IS NOT NULL AND dt_saida_local::text <> '') and (dt_chegada_base IS NOT NULL AND dt_chegada_base::text <> '')))
and	nr_sequencia = nr_sequencia_w;

select	max(dt_partida),
	max(dt_chegada_local),
	max(dt_saida_local),
	max(dt_chegada_base)
into STRICT	dt_partida_w,
        dt_chegada_local_w,
        dt_saida_local_w,
        dt_chegada_base_w
from	EME_REG_RESPOSTA
where	nr_seq_equipe = nr_seq_equipe_p
and	nr_Sequencia = nr_sequencia_w;

select	coalesce(max(nr_sequencia),0)
into STRICT	ie_status_not_liberada_w
from	eme_reg_resposta a
where	nr_seq_equipe = nr_seq_equipe_p
and	   (((dt_partida IS NOT NULL AND dt_partida::text <> '') and coalesce(dt_chegada_local::text, '') = '' and coalesce(dt_saida_local::text, '') = '' and coalesce(dt_chegada_base::text, '') = '') or ((dt_partida IS NOT NULL AND dt_partida::text <> '') and (dt_chegada_local IS NOT NULL AND dt_chegada_local::text <> '') and coalesce(dt_saida_local::text, '') = '' and coalesce(dt_chegada_base::text, '') = ''))
and	nr_sequencia = nr_sequencia_w;

select	coalesce(max(ie_almoco),'N'),
	coalesce(max(nr_seq_motivo_parada),0)
into STRICT	ie_almoco_w,
	nr_seq_motivo_parada_w
from	pf_equipe
where	nr_sequencia = nr_seq_equipe_p;

select 	ie_situacao
into STRICT	ie_situacao_w
from	pf_equipe
where	nr_sequencia = nr_seq_equipe_p;

if (ie_situacao_w = 'A') then
	if (dt_partida_w IS NOT NULL AND dt_partida_w::text <> '') and (coalesce(dt_chegada_local_w::text, '') = '') then
		ds_retorno_w	:= 'J9';
	elsif (dt_chegada_local_w IS NOT NULL AND dt_chegada_local_w::text <> '') and (coalesce(dt_saida_local_w::text, '') = '') then
		ds_retorno_w	:= 'J10';
	elsif (dt_saida_local_w IS NOT NULL AND dt_saida_local_w::text <> '') and (coalesce(dt_chegada_base_w::text, '') = '') then
		ds_retorno_w	:= 'J11';
	elsif (ie_almoco_w = 'S') then
		ds_retorno_w := 'J4';
	elsif (nr_seq_motivo_parada_w <> 0) then
		ds_retorno_w := 'J7';
	elsif (dt_chegada_base_w IS NOT NULL AND dt_chegada_base_w::text <> '') then
		ds_retorno_w := 'J12';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_reg_equipe (nr_seq_equipe_p bigint) FROM PUBLIC;

