CREATE OR REPLACE FUNCTION obter_status_requisito (nr_seq_prs_p bigint) RETURNS bigint AS $body$
DECLARE


retorno_w smallint := 2;
possui_registro_w varchar(1) := 'N';


BEGIN

    select CASE WHEN count(*)=0 THEN  'N'                                ELSE 'S' END  ie_status
    into STRICT possui_registro_w
			from LATAM_REQUISITO_PRS y
			where y.nr_sequencia = nr_seq_prs_p
			and (y.NR_SEQ_PRS IS NOT NULL AND y.NR_SEQ_PRS::text <> '')
			and not exists ( SELECT 1
			from LATAM_REQUISITO x
			where x.NR_SEQ_PRS = y.NR_SEQUENCIA);

    if (possui_registro_w = 'S') then
      retorno_w := 0;
    else
      select CASE WHEN count(*)=0 THEN  'N'                                ELSE 'S' END  ie_status
      into STRICT possui_registro_w
      from LATAM_REQUISITO_PRS y
      where y.nr_sequencia = nr_seq_prs_p
      and (y.NR_SEQ_PRS IS NOT NULL AND y.NR_SEQ_PRS::text <> '')
      and exists ( SELECT 1
      from LATAM_REQUISITO x
      where x.NR_SEQ_PRS = y.NR_SEQUENCIA);
      if (possui_registro_w = 'S') then
        retorno_w := 1;
      end if;
    end if;
	
	return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
