CREATE OR REPLACE FUNCTION obter_status_req_medispan ( cd_api_p alerta_api.cd_api%type, nr_seq_controle_p alerta_api.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w alerta_api.ie_status_requisicao%type;


BEGIN
	ds_retorno_w := 'X';

	if (nr_seq_controle_p IS NOT NULL AND nr_seq_controle_p::text <> '') then
		select	coalesce(max(ie_status_requisicao), 'X')
		  into STRICT	ds_retorno_w
		  from	alerta_api 
		 where	nr_sequencia = nr_seq_controle_p
		   and	cd_api = cd_api_p
		   and	(ie_status_requisicao IS NOT NULL AND ie_status_requisicao::text <> '');
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
