SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_resposta_ian (nr_seq_aval_pessoa_p bigint) RETURNS varchar AS $body$
DECLARE


/*
N - N達o respondindo
S - Respondido
P - Parcialmente respondido
X - Perguntas n達o cadastradas
*/
qt_resposta_w		bigint;
qt_pergunta_w		bigint;
nr_seq_versao_w		bigint;
vl_importancia_w	bigint;

BEGIN

select	nr_seq_versao
into STRICT	nr_seq_versao_w
from	ian_avaliacao
where	nr_sequencia = (SELECT nr_seq_avaliacao from ian_avaliacao_pessoa where nr_sequencia = nr_seq_aval_pessoa_p);

select	count(*),
	sum(vl_importancia)
into STRICT	qt_resposta_w,
	vl_importancia_w
from	ian_nec_pessoal_resposta
where	nr_seq_aval_pessoa = nr_seq_aval_pessoa_p;

select	count(*)
into STRICT	qt_pergunta_w
from	ian_nec_pessoal a
where	a.nr_seq_nec_motiv = (	SELECT 	x.nr_sequencia
				from 	ian_nec_motiv x
				where 	x.nr_seq_versao = nr_seq_versao_w
				and 	x.nr_sequencia = a.nr_seq_nec_motiv);

if (qt_pergunta_w = 0) then
	return 'X'; --perguntas n達o cadastradas
elsif (qt_resposta_w = 0) then
	return 'N'; --N達o respondido
elsif	((qt_resposta_w < qt_pergunta_w) or (coalesce(vl_importancia_w::text, '') = '') or (vl_importancia_w = 0)) then
	return 'P'; --parcialmente
else
	return 'S'; --respondido
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_resposta_ian (nr_seq_aval_pessoa_p bigint) FROM PUBLIC;

