CREATE OR REPLACE FUNCTION obter_status_ret_conv ( nr_seq_ret_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_retorno_w	varchar(1);


BEGIN

begin
select	a.ie_status_retorno
into STRICT	ie_status_retorno_w
from	convenio_retorno a,
	convenio_retorno_item b
where	b.nr_seq_retorno	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_ret_item_p  LIMIT 1;
exception
when others then
	ie_status_retorno_w	:= 'R';
end;

return	ie_status_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
