CREATE OR REPLACE FUNCTION obter_status_san_transfusao (nr_seq_transfusao_p bigint, nr_seq_reserva_hemo_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ie_status_w             varchar(10);
nr_seq_transfusao_w     SAN_TRANSFUSAO.nr_sequencia%type;


BEGIN

if (nr_seq_transfusao_p = 0 and nr_seq_reserva_hemo_p <> 0 )then

  select max(nr_sequencia)
    into STRICT nr_seq_transfusao_w   
  from san_transfusao
  where nr_seq_reserva = nr_seq_reserva_hemo_p;

end if;

if ((nr_seq_transfusao_p IS NOT NULL AND nr_seq_transfusao_p::text <> '') or (nr_seq_transfusao_w IS NOT NULL AND nr_seq_transfusao_w::text <> '')) then

  select  max(ie_status)
    into STRICT  ie_status_w
  from  san_transfusao
  where (nr_sequencia = nr_seq_transfusao_p or nr_sequencia = nr_seq_transfusao_w);

end if;

return  ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
