SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_schematic4test (NR_SEQ_SCRIPT_P bigint, NR_SEQ_SCHEDULE_P bigint) RETURNS bigint AS $body$
DECLARE


STATUS_W varchar(255);
QTD_W bigint;


BEGIN
	--function that return status of script
	IF (NR_SEQ_SCRIPT_P IS NOT NULL AND NR_SEQ_SCRIPT_P::text <> '' AND NR_SEQ_SCHEDULE_P IS NOT NULL AND NR_SEQ_SCHEDULE_P::text <> '') THEN
		SELECT COUNT(NR_SEQUENCIA)
			INTO STRICT QTD_W
		FROM SCHEM_TEST_LOG 		
		WHERE NR_SEQ_SCHEDULE = NR_SEQ_SCHEDULE_P 
		AND NR_SEQ_SCRIPT = NR_SEQ_SCRIPT_P
		AND IE_STATUS = '1';

		IF (QTD_W <> 0) THEN
         	STATUS_W := '1';
       	 END IF;

		SELECT COUNT(NR_SEQUENCIA)
    		INTO STRICT QTD_W
		FROM SCHEM_TEST_LOG 	
		WHERE NR_SEQ_SCHEDULE = NR_SEQ_SCHEDULE_P
		AND NR_SEQ_SCRIPT = NR_SEQ_SCRIPT_P
		AND IE_STATUS = '2';

		IF (QTD_W <> 0) THEN
          	STATUS_W := '2';
        END IF;

		SELECT COUNT(NR_SEQUENCIA)
			INTO STRICT QTD_W
		FROM SCHEM_TEST_LOG 	
		WHERE NR_SEQ_SCHEDULE = NR_SEQ_SCHEDULE_P
		AND NR_SEQ_SCRIPT = NR_SEQ_SCRIPT_P
		AND IE_STATUS = '4';

		IF (QTD_W <> 0) THEN
         	STATUS_W := '4';
       	END IF;
END IF;

RETURN STATUS_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
