SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_solucao_cpoe (nr_seq_cpoe_p cpoe_material.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_status_w         	varchar(3);
nr_sequencia_w      	prescr_solucao_evento.nr_sequencia%type;
qt_vol_infundido_w	prescr_solucao_evento.qt_vol_infundido%type;
qt_vol_desprezado_w	prescr_solucao_evento.qt_vol_desprezado%type;


BEGIN

select  coalesce(max('S'),'N')
into STRICT    ie_status_w
from	prescr_solucao a,
        prescr_material b
where	a.nr_prescricao	= b.nr_prescricao
and     a.nr_seq_solucao = b.nr_sequencia_solucao
and	    b.nr_seq_mat_cpoe = nr_seq_cpoe_p
and     a.ie_suspenso = 'S';

if (ie_status_w <> 'S') then
    ie_status_w := '';
    /* obter sequencia evento */

    select	coalesce(max(nr_sequencia),0)
    into STRICT	nr_sequencia_w
    from	prescr_solucao_evento
    where	nr_seq_cpoe = nr_seq_cpoe_p
    and	ie_alteracao	in (1,2,3,4,35)
    and	ie_evento_valido	= 'S';

    /* obter status solucao */

    if (nr_sequencia_w > 0) then
        select	CASE WHEN ie_alteracao=1 THEN  'I' WHEN ie_alteracao=2 THEN  'INT' WHEN ie_alteracao=3 THEN  'R' WHEN ie_alteracao=4 THEN  'T' WHEN ie_alteracao=35 THEN 'I' END
        into STRICT	ie_status_w
        from	prescr_solucao_evento
        where	nr_sequencia = nr_sequencia_w;

        /* tratar interromper sobre interromper */

        if (ie_status_w = 'INT') then

            select	coalesce(max(qt_vol_infundido),-1),
                coalesce(max(qt_vol_desprezado),-1)
            into STRICT	qt_vol_infundido_w,
                qt_vol_desprezado_w
            from	prescr_solucao_evento
            where	nr_sequencia = nr_sequencia_w;

            if (qt_vol_infundido_w = -1) and (qt_vol_desprezado_w = -1) then
                ie_status_w := 'II';
            end if;
        end if;
    else
        ie_status_w := 'N';
    end if;
end if;
return ie_status_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
