SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_teste_proj ( NR_OS_ADERENCIA_P bigint) RETURNS varchar AS $body$
DECLARE

  QTD_ENC_W     bigint      := 0;
  QTD_INI_W     bigint      := 0;
  PR_RETORNO_W	varchar(1) := '';


BEGIN

	BEGIN
    SELECT COUNT(1)
      INTO STRICT QTD_ENC_W
      FROM MAN_ORDEM_SERVICO OS
     WHERE OS.NR_SEQUENCIA    = NR_OS_ADERENCIA_P
       AND OS.NR_SEQ_ESTAGIO IN (9);
	EXCEPTION
		WHEN no_data_found THEN
			QTD_ENC_W := 0;
		WHEN OTHERS THEN
			QTD_ENC_W := 0;
	END;

	BEGIN
    SELECT COUNT(1)
      INTO STRICT QTD_INI_W
      FROM MAN_ORDEM_SERV_ATIV ASP,
           USUARIO U
     WHERE ASP.NM_USUARIO         = U.NM_USUARIO
       AND ASP.NR_SEQ_ORDEM_SERV  = NR_OS_ADERENCIA_P
       AND U.CD_SETOR_ATENDIMENTO = 12;
	EXCEPTION
		WHEN no_data_found THEN
			QTD_INI_W := 0;
		WHEN OTHERS THEN
			QTD_INI_W := 0;
	END;

  IF QTD_ENC_W > 0 THEN
    PR_RETORNO_W := 'E';
  ELSIF QTD_INI_W > 0 THEN
    PR_RETORNO_W := 'I';
  ELSE
    PR_RETORNO_W := 'P';
  END IF;

  RETURN	PR_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_teste_proj ( NR_OS_ADERENCIA_P bigint) FROM PUBLIC;

