SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_tratamento_mipres ( nr_seq_prescr_mipres_p prescr_mipres.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_status_tratamento_w 	varchar(3);
qt_mipres_w             bigint;
nr_prescricao_mipres_w	prescr_mipres.nr_prescricao%type;
dt_validity_mipres_w    prescr_mipres.dt_validity_mipres%type;


BEGIN
	/*
	[NOI] Not informed.
	[EXP] MIPRES expired.
	*/
	ie_status_tratamento_w := null;
	
	if (nr_seq_prescr_mipres_p IS NOT NULL AND nr_seq_prescr_mipres_p::text <> '') then
		
		select CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
		into STRICT qt_mipres_w
		from prescr_mipres
		where nr_sequencia = nr_seq_prescr_mipres_p;

		if (qt_mipres_w = 'S') then
		
			select max(nr_prescricao),
			max(dt_validity_mipres)
			into STRICT nr_prescricao_mipres_w,
			dt_validity_mipres_w
			from prescr_mipres
			where nr_sequencia = nr_seq_prescr_mipres_p;
			
			if (coalesce(nr_prescricao_mipres_w::text, '') = '') then
				ie_status_tratamento_w := 'NOI';
			end if;
			
			if ((dt_validity_mipres_w IS NOT NULL AND dt_validity_mipres_w::text <> '') and dt_validity_mipres_w < clock_timestamp()) then
				ie_status_tratamento_w := 'EXP';
			end if;
		
		else
			ie_status_tratamento_w := 'NOI';
		end if;
	
	end if;
		
    return ie_status_tratamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
