CREATE OR REPLACE FUNCTION obter_status_unid_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w	varchar(10) := '';				
 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select 	coalesce(ie_status_unidade,'L') ie_status_unidade 
	into STRICT	ds_retorno_w 
	from	unidade_atendimento 
	where	nr_seq_interno = obter_unidade_atendimento(nr_atendimento_p,'A','NR');
end if;
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
