SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_worklist ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  		varchar(100);
ie_retorno_w  		varchar(100);
dt_inicio_w   		timestamp;
dt_final_previsto_w timestamp;
dt_final_real_w     timestamp;
qt_tempo_atraso_w	wl_regra_item.qt_tempo_atraso%type;

/*
 A - Atrasa/Delayed
 T - Em tempo/On time
 C - Concluido/Done

 ie_opcao_p
   D - Description
   C - Code
 */
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	dt_inicial,
			dt_final_previsto,
			dt_final_real
	into STRICT	dt_inicio_w,
			dt_final_previsto_w,
			dt_final_real_w
	from 	wl_worklist
	where 	nr_sequencia = nr_sequencia_p;

	select	max(a.qt_tempo_atraso)
	into STRICT	qt_tempo_atraso_w
	from	wl_regra_item a,
			wl_worklist b
	where	a.nr_sequencia = b.nr_seq_regra
	and		b.nr_sequencia = nr_sequencia_p;

	if (coalesce(qt_tempo_atraso_w::text, '') = '') then
		if (clock_timestamp() > dt_final_previsto_w and coalesce(dt_final_real_w::text, '') = '') then
			begin
			ds_retorno_w := obter_desc_expressao(302651); -- delayed
			ie_retorno_w := 'A';
			end;
		elsif (dt_inicio_w <= dt_final_previsto_w and coalesce(dt_final_real_w::text, '') = '') then
			begin
			ds_retorno_w := obter_desc_expressao(560323); -- in progress
			ie_retorno_w := 'T';
			end;
		elsif (dt_final_real_w IS NOT NULL AND dt_final_real_w::text <> '') then
			begin
			ds_retorno_w := obter_desc_expressao(654443); -- done
			ie_retorno_w := 'C';
			end;
		end if;

	else
		if (clock_timestamp() > (dt_inicio_w+(qt_tempo_atraso_w/24)) and coalesce(dt_final_real_w::text, '') = '') then
			begin
			ds_retorno_w := obter_desc_expressao(302651); -- delayed
			ie_retorno_w := 'A';
			end;
		elsif (dt_inicio_w <= dt_final_previsto_w and coalesce(dt_final_real_w::text, '') = '') then
			begin
			ds_retorno_w := obter_desc_expressao(560323); -- in progress
			ie_retorno_w := 'T';
			end;
		elsif (dt_final_real_w IS NOT NULL AND dt_final_real_w::text <> '') then
			begin
			ds_retorno_w := obter_desc_expressao(654443); -- done
			ie_retorno_w := 'C';
			end;
		end if;

	end if;

	if (ie_opcao_p = 'D') then
		return ds_retorno_w;
	else
		return ie_retorno_w;
	end if;
end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
