SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_stratify ( dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* 
Total Pacientes Dia			'PD' 
Total Pacientes Dia Internado		'PI' 
*/
			 
ds_retorno_w	bigint;
	

BEGIN 
 
 
if (ie_opcao_p = 'PD') then 
 
	select	count(distinct nr_atendimento) 
	into STRICT	ds_retorno_w 
	from	resumo_atendimento_paciente_v 
	where	trunc(dt_entrada) <= trunc(dt_inicio_p) 
	and	coalesce(dt_alta,fim_dia(dt_inicio_p)) >= fim_dia(dt_inicio_p);
 
elsif (ie_opcao_p = 'PI') then 
	 
	select	count(distinct a.nr_atendimento) 
	into STRICT	ds_retorno_w 
	from	paciente_internado_v2 a 
	where	trunc(a.dt_entrada_unidade) <= trunc(dt_inicio_p) 
	and		coalesce(a.dt_saida_interno,fim_dia(dt_inicio_p)) >= fim_dia(dt_inicio_p);
	 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
