CREATE OR REPLACE FUNCTION obter_string_posicao ( string_p text, inicial_p bigint, posicao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_posicao_w			varchar(2000);
ds_terminar_w				varchar(1) := 'X';
ds_inicial_w				varchar(1) := 'X';
pos_inicial_w				bigint;
ds_retorno_w				varchar(2000);


BEGIN

pos_inicial_w	:= inicial_p;

select	substr(string_p,pos_inicial_w,posicao_p)
into STRICT	ds_retorno_posicao_w
;

if (pos_inicial_w = 0) then
	while(ds_terminar_w <> ',') loop

	select	substr(ds_retorno_posicao_w,length(ds_retorno_posicao_w),1)
	into STRICT	ds_terminar_w
	;

	if (ds_terminar_w = ',') then
		ds_retorno_w := substr(ds_retorno_posicao_w,1,length(ds_retorno_posicao_w)-1);
	else
		ds_retorno_posicao_w := substr(ds_retorno_posicao_w,1,length(ds_retorno_posicao_w)-1);
	end if;
	end loop;
else
	while(ds_inicial_w <> ',') loop

	select	substr(string_p,pos_inicial_w,1)
	into STRICT	ds_inicial_w
	;
	if (ds_inicial_w = ',') then
		ds_retorno_posicao_w := substr(string_p,pos_inicial_w +1,posicao_p);
	else
		pos_inicial_w := pos_inicial_w - 1;
	end if;
	end loop;
	while(ds_terminar_w <> ',') loop
	select	substr(ds_retorno_posicao_w,length(ds_retorno_posicao_w),1)
	into STRICT	ds_terminar_w
	;

	if (ds_terminar_w = ',') then
		ds_retorno_w := substr(ds_retorno_posicao_w,1,length(ds_retorno_posicao_w)-1);
	else
		ds_retorno_posicao_w := substr(ds_retorno_posicao_w,1,length(ds_retorno_posicao_w)-1);
	end if;
	end loop;

end if;

return	ds_retorno_w;

end	;
$body$
