CREATE OR REPLACE FUNCTION obter_subst_generic_medic (cd_protocolo_p bigint, nr_sequencia_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_atendimento_cpoe_p bigint default null) RETURNS bigint AS $body$
DECLARE


cd_material_w		bigint;
cd_material_gen_w	bigint;


BEGIN

select	coalesce(max(a.cd_material), 0)
into STRICT 	cd_material_w
from	protocolo_medic_material a,
		material b
where  	nr_seq_solucao = nr_seq_solucao_p
and 	cd_protocolo   = cd_protocolo_p
and   	nr_sequencia   = nr_sequencia_p
and		a.cd_material  = b.cd_material
and 	b.ie_ancora_solucao = 'S'
and   	obter_se_material_ativo(a.cd_material) = 'A'
and   	not exists (SELECT   1
			from     rep_item_excl_protocolo x
			where    x.nr_prescricao   = nr_prescricao_p
			and      x.cd_protocolo    = cd_protocolo_p
			and      x.nr_seq_item     = nr_sequencia_p
			and      x.nr_seq_material = a.nr_seq_material);

cd_material_gen_w := coalesce(obter_regra_subst_com(nr_prescricao_p, cd_material_w, nr_atendimento_cpoe_p), 8);

return	cd_material_gen_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
