CREATE OR REPLACE FUNCTION obter_subtipo_billing (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_tipo_w		varchar(2255);			
			 

BEGIN 
 
 
select 	max(ds_subtipo) 
into STRICT 	ds_tipo_w 
from 	episodio_paciente a, 
	atendimento_paciente b, 
	subtipo_episodio c 
where	a.nr_sequencia = b.nr_seq_episodio 
and 	a.nr_seq_subtipo_episodio = c.nr_sequencia 
and 	b.nr_atendimento = nr_atendimento_p;
 
 
return	ds_tipo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
