SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_suep_padrao_dashboard ( nr_atendimento_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

				
nr_seq_suep_w			bigint;		
nr_suep_lib_w			bigint;
cd_pessoa_usuario_w		varchar(100) := '';
	

BEGIN

	select	obter_pf_usuario(nm_usuario_p,'C')
	into STRICT	cd_pessoa_usuario_w
	;
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_suep_w
	from	suep a
	where	obter_se_suep_liberado_cid(nr_sequencia,cd_pessoa_usuario_w,nr_atendimento_p) = 'S'
	AND 	coalesce(a.ie_situacao,'A') = 'A'
	AND     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	coalesce(ie_sumario_exclusivo,'N') = 'N';
	
	if (nr_seq_suep_w > 0) then
		return nr_seq_suep_w;
	end if;

	select	coalesce(max(a.nr_seq_modelo_suep),0)
	into STRICT	nr_seq_suep_w
	from	suep_padrao_usuario a,
			suep b
	where	a.nr_seq_modelo_suep = b.nr_sequencia
	AND 	coalesce(b.ie_situacao,'A') = 'A'
	AND     (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	AND		a.nm_usuario_suep = nm_usuario_p;
	
	if (coalesce(nr_seq_suep_w::text, '') = '' or nr_seq_suep_w = 0) then
	
		select	count(*), max(nr_sequencia)
		into STRICT	nr_suep_lib_w, nr_seq_suep_w
		from	suep a
		where	obter_se_suep_liberado_usuario(nr_sequencia,cd_pessoa_usuario_w,nr_atendimento_p) = 'S'
		AND 	coalesce(a.ie_situacao,'A') = 'A'
		AND     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	coalesce(ie_sumario_exclusivo,'N') = 'N';

		if (nr_suep_lib_w = 1) then
			return nr_seq_suep_w;
		end if;	
	
		select	max(b.nr_sequencia)
		into STRICT	nr_seq_suep_w
		from	regra_liberacao_suep a,
				suep b
		where	a.nr_seq_suep = b.nr_sequencia
		and		((a.cd_perfil = wheb_usuario_pck.get_cd_perfil) or (coalesce(cd_perfil::text, '') = ''))
		and     ((a.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(a.cd_estabelecimento::text, '') = ''))
		and     ((a.cd_setor_atendimento = wheb_usuario_pck.get_cd_setor_atendimento) or (coalesce(a.cd_setor_atendimento::text, '') = ''))
		and		((obter_se_especialidade_medico(cd_pessoa_usuario_w, a.cd_especialidade) = 'S') or (coalesce(a.cd_especialidade::text, '') = ''))
		and		((obter_se_atend_cid(a.cd_doenca, nr_atendimento_p) = 'S') or (coalesce(a.cd_doenca::text, '') = ''))
		and		regra_lib_suep_complementar(nr_atendimento_p, a.nr_seq_suep) = 'X'
		and 	coalesce(b.ie_situacao,'A') = 'A'
		and     (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and		coalesce(a.ie_padrao,'N') = 'S'
		and		coalesce(b.ie_sumario_exclusivo,'N') = 'N';
		
		if (coalesce(nr_seq_suep_w,0) > 1) then
			return nr_seq_suep_w;
		end if;	
		
		
		if (nr_suep_lib_w > 1 or coalesce(nr_seq_suep_w,0) = 0) then
			return -1;
		end if;
		
	end if;

	return	nr_seq_suep_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_suep_padrao_dashboard ( nr_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

