CREATE OR REPLACE FUNCTION obter_superficie_corporea_red ( qt_peso_p bigint, qt_altura_p bigint, qt_porcent_reducao_p bigint, IE_FORMULA_SUP_CORPOREA_P text default null) RETURNS bigint AS $body$
DECLARE


