CREATE OR REPLACE FUNCTION obter_sup_regra_local_cc_nf ( cd_operacao_nf_p bigint, cd_centro_custo_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_existe_w		integer;
cd_local_estoque_w	bigint;
qt_existe_estab_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	sup_regra_local_cc_nf;

if (qt_existe_w > 0) and (cd_centro_custo_p IS NOT NULL AND cd_centro_custo_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_existe_estab_w
	from	sup_regra_local_cc_nf
	where	cd_centro_custo = cd_centro_custo_p
	and 	cd_operacao_nf = cd_operacao_nf_p
	and	cd_estabelecimento = cd_estabelecimento_p;

	if (qt_existe_estab_w > 0) then
		begin

		select	max(cd_local_estoque)
		into STRICT	cd_local_estoque_w
		from	sup_regra_local_cc_nf
		where	cd_centro_custo = cd_centro_custo_p
		and 	cd_operacao_nf = cd_operacao_nf_p
		and 	cd_estabelecimento = cd_estabelecimento_p;
		exception when others then
			cd_local_estoque_w	:= 0;

		end;

	else
		begin

		select	max(cd_local_estoque)
		into STRICT	cd_local_estoque_w
		from	sup_regra_local_cc_nf
		where	cd_centro_custo = cd_centro_custo_p
		and 	cd_operacao_nf = cd_operacao_nf_p;
		exception when others then
			cd_local_estoque_w	:= 0;
		end;
	end if;

	end;
end if;

if (cd_local_estoque_w > 0) then
	return cd_local_estoque_w;
end if;

end;
