CREATE OR REPLACE FUNCTION obter_sus_consiste_campo_apac ( nm_campo_p text, nr_apac_p bigint, nr_seq_apac_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_cid_princ_p text, cd_cid_sec_p text, cd_cid_assoc_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
 
ds_erro_w		varchar(2000)	:= '';
ds_erro_apac_w		varchar(2000)	:= '';
ds_erro_cid_w		varchar(2000)	:= '';
ds_inco_w		varchar(255);
cd_inconsistencia_w	bigint;
ds_inconsistencia_w	varchar(255);
ds_retorno_w		varchar(2000);
i			integer;

 

BEGIN 
if	((nm_campo_p	=	'CD_CID_PRINCIPAL') or (nm_campo_p	=	'CD_ESTADIO')) then 
	ds_erro_cid_w := Sus_Consiste_Dados_Apac(nm_campo_p, nr_apac_p, nr_seq_apac_p, cd_procedimento_p, ie_origem_proced_p, cd_cid_princ_p, nm_usuario_p, 2, ds_erro_cid_w);
elsif (nr_apac_p	> 0) then 
	ds_erro_apac_w := Sus_Consiste_Numero_Apac(0, nr_apac_p, nm_usuario_p, 2, ds_erro_apac_w);
end if;
 
ds_erro_w	:= ds_erro_apac_w || ds_erro_cid_w;
 
/* Teste se há algum erro */
 
if (length(ds_erro_w) > 0) then 
	ds_inco_w	:= ds_erro_w;
	while (ds_inco_w IS NOT NULL AND ds_inco_w::text <> '') LOOP 
		begin 
		/* Busca a posição do separados das inconsistencias, no caso a virgula */
 
		i	:= position(', ' in ds_inco_w);
		if (i > 1) then 
			cd_inconsistencia_w	:= substr(ds_inco_w, 1, i-1);
			ds_inco_w		:= substr(ds_inco_w, i+1, 255);	
		end if;
		/* Se existir apenas um espaço em branco entao limpa a variavel para sair do loop */
 
		if (ds_inco_w = ' ') then 
			ds_inco_w	:= '';
		end if;
		 
		/* Busca a descricao da inconsistencia */
 
		select	coalesce(max(ds_inconsistencia),'') 
		into STRICT	ds_inconsistencia_w 
		from	sus_inconsistencia 
		where	cd_inconsistencia	= cd_inconsistencia_w;
 
		ds_retorno_w	:= ds_retorno_w || ' - ' || ds_inconsistencia_w || chr(10);
 
		end;
	END LOOP;
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
