CREATE OR REPLACE FUNCTION obter_sus_grau_instrucao (nr_aih_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_atendimento_w	bigint;
ie_grau_instrucao_w	smallint;
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
select	nr_atendimento, 
	ie_grau_instrucao 
into STRICT	nr_atendimento_w, 
	ie_grau_instrucao_w 
from	sus_aih 
where 	nr_aih 		= nr_aih_p 
and	nr_sequencia	= nr_sequencia_p;
 
if (coalesce(ie_grau_instrucao_w::text, '') = '') then 
	select	CASE WHEN b.ie_grau_instrucao=1 THEN 1 WHEN b.ie_grau_instrucao=2 THEN 2 WHEN b.ie_grau_instrucao=3 THEN 3 WHEN b.ie_grau_instrucao=4 THEN 4 WHEN b.ie_grau_instrucao=5 THEN 4 WHEN b.ie_grau_instrucao=6 THEN 4 WHEN b.ie_grau_instrucao=7 THEN 2 WHEN b.ie_grau_instrucao=8 THEN 3 WHEN b.ie_grau_instrucao=9 THEN 4 WHEN b.ie_grau_instrucao=10 THEN 2 WHEN b.ie_grau_instrucao=11 THEN 2  ELSE null END  
	into STRICT	ie_grau_instrucao_w 
	from	pessoa_fisica b, 
		atendimento_paciente a 
	where a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	 and a.nr_atendimento = nr_atendimento_w;
end if;
 
return ie_grau_instrucao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
