SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tabela_evento_integr ( nr_seq_integracao_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1) := 'N';
ie_evento_w		varchar(15);
qt_existe_w		bigint;



BEGIN

select	ie_evento
into STRICT	ie_evento_w
from	sup_parametro_integracao
where	nr_sequencia = nr_seq_integracao_p;

select	count(*)
into STRICT	qt_existe_w
from	sup_int_conv_tabela
where	nm_tabela = nm_tabela_p
and	ie_evento = ie_evento_w;

if (qt_existe_w > 0) then
	ie_permite_w := 'S';
end if;

return	coalesce(ie_permite_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
