CREATE OR REPLACE FUNCTION obter_tabela_hist_saude (ie_hist_saude_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';

BEGIN
	if (ie_hist_saude_p IS NOT NULL AND ie_hist_saude_p::text <> '') then
		if (ie_hist_saude_p = 'R') then
			ds_retorno_w := 'PACIENTE_ALERGIA';
		elsif (ie_hist_saude_p = 'H') then
			ds_retorno_w := 'PACIENTE_HABITO_VICIO';
		elsif (ie_hist_saude_p = 'M') then
			ds_retorno_w := 'PACIENTE_MEDIC_USO';
		elsif (ie_hist_saude_p = 'DP') then
			ds_retorno_w := 'PACIENTE_ANTEC_CLINICO';
		elsif (ie_hist_saude_p = 'T') then
			ds_retorno_w := 'PESSOA_FISICA';
		elsif (ie_hist_saude_p = 'B') then
			ds_retorno_w := 'ATENDIMENTO_PACIENTE';
		elsif (ie_hist_saude_p = 'CE') then
			ds_retorno_w := 'HISTORICO_SAUDE_CIRURGIA';
		elsif (ie_hist_saude_p = 'C') then
			ds_retorno_w := 'CIRURGIA';
		elsif (ie_hist_saude_p = 'O') then
			ds_retorno_w := 'PACIENTE_OCORRENCIA';
		elsif (ie_hist_saude_p = 'D') then
			ds_retorno_w := 'DIAGNOSTICO_DOENCA';
		elsif (ie_hist_saude_p = 'V') then
			ds_retorno_w := 'PACIENTE_VACINA';
		elsif (ie_hist_saude_p = 'E') then
			ds_retorno_w := 'PACIENTE_EXAME';
		elsif (ie_hist_saude_p = 'TR') then
			ds_retorno_w := 'PACIENTE_TRANSFUSAO';
		elsif (ie_hist_saude_p = 'TH') then
			ds_retorno_w := 'SAN_TRANSFUSAO';
		end if;
	end if;

return ds_retorno_w;

end;
