CREATE OR REPLACE FUNCTION obter_tabela_sistema_idioma ( nm_tabela_p text, nr_seq_visao_p bigint, nr_seq_idioma_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

BEGIN

if (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nr_seq_idioma_p > 0) then

	if (nr_seq_visao_p IS NOT NULL AND nr_seq_visao_p::text <> '') and (nr_seq_visao_p > 0) then
		select	max(ds_traducao)
		into STRICT	ds_retorno_w
		from	tab_sistema_idioma
		where	nm_tabela = nm_tabela_p
		and	nr_seq_visao = nr_seq_visao_p
		and	nr_seq_idioma = nr_seq_idioma_p;
	else
		select	max(ds_traducao)
		into STRICT	ds_retorno_w
		from	tab_sistema_idioma
		where	nm_tabela = nm_tabela_p
		and	coalesce(nr_seq_visao::text, '') = ''
		and	nr_seq_idioma = nr_seq_idioma_p;
	end if;

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and (ds_retorno_w <> ' ') then
		return ds_retorno_w;
	end if;

end if;

if (nr_seq_visao_p IS NOT NULL AND nr_seq_visao_p::text <> '') and (nr_seq_visao_p > 0) then
	select	ds_titulo
	into STRICT	ds_retorno_w
	from	tabela_visao
	where	nr_sequencia = nr_seq_visao_p;
else
	select	ds_titulo
	into STRICT	ds_retorno_w
	from	tabela_sistema
	where	nm_tabela = nm_tabela_p;
end if;

return	ds_retorno_w;

end;
$body$
