SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tag_categ_int_me (nr_solic_compra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_solic_item_w		timestamp;
ds_dias_solic_w		varchar(255);
qt_dias_solic_w		bigint;
qt_regra_grupo_w	bigint := 0;
qt_subgrupo_material_w	bigint := 0;
qt_classe_material_w	bigint := 0;
qt_cd_material_w	bigint := 0;
cd_material_w		material.cd_material%type;
cd_grupo_material_w	grupo_material.cd_grupo_material%type;
cd_subgrupo_material_w	subgrupo_material.cd_subgrupo_material%type;
cd_classe_material_w	classe_material.cd_classe_material%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_seq_motivo_urgente_w	solic_compra.nr_seq_motivo_urgente%type;
qt_registros_w		bigint;

-- opcao_p =  'C' código 'D' descrição
c01 CURSOR FOR
	SELECT	cd_prioridade_entrega,
		ds_prioridade_entrega,
		coalesce(qt_dias_min_entrega,0) qt_dias_min_entrega,
		nr_seq_motivo_urgente
	from	regra_envio_prioridade_int
	where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	coalesce(nr_seq_motivo_urgente,nr_seq_motivo_urgente_w) = nr_seq_motivo_urgente_w
	and	((coalesce(cd_grupo_material::text, '') = '') and (qt_regra_grupo_w = 0))
	and	((coalesce(cd_subgrupo_material::text, '') = '') and (qt_subgrupo_material_w = 0))
	and	((coalesce(cd_classe_material::text, '') = '') and (qt_classe_material_w = 0))
	and	((coalesce(cd_material::text, '') = '') and (qt_cd_material_w = 0))
	
union all

	/*somente Grupo de materiais */

	SELECT	cd_prioridade_entrega,
		ds_prioridade_entrega,
		coalesce(qt_dias_min_entrega,0) qt_dias_min_entrega,
		nr_seq_motivo_urgente
	from	regra_envio_prioridade_int
	where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	coalesce(nr_seq_motivo_urgente,nr_seq_motivo_urgente_w) = nr_seq_motivo_urgente_w
	and	(cd_grupo_material IS NOT NULL AND cd_grupo_material::text <> '')
	and	cd_grupo_material = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material::text, '') = ''
	and	coalesce(cd_classe_material::text, '') = ''
	and	coalesce(cd_material::text, '') = ''
	
union all

	/*somente subgrupo material */

	select	cd_prioridade_entrega,
		ds_prioridade_entrega,
		coalesce(qt_dias_min_entrega,0) qt_dias_min_entrega,
		nr_seq_motivo_urgente
	from	regra_envio_prioridade_int
	where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	coalesce(nr_seq_motivo_urgente,nr_seq_motivo_urgente_w) = nr_seq_motivo_urgente_w
	and	(cd_subgrupo_material IS NOT NULL AND cd_subgrupo_material::text <> '')
	and	cd_subgrupo_material = cd_subgrupo_material_w
	and	coalesce(cd_classe_material::text, '') = ''
	and	coalesce(cd_material::text, '') = ''
	
union all

	/*somente classe material*/

	select	cd_prioridade_entrega,
		ds_prioridade_entrega,
		coalesce(qt_dias_min_entrega,0) qt_dias_min_entrega,
		nr_seq_motivo_urgente
	from	regra_envio_prioridade_int
	where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	coalesce(nr_seq_motivo_urgente,nr_seq_motivo_urgente_w) = nr_seq_motivo_urgente_w
	and	(cd_classe_material IS NOT NULL AND cd_classe_material::text <> '')
	and	cd_classe_material = cd_classe_material_w
	and	coalesce(cd_material::text, '') = ''
	
union all

	/*somente material*/

	select	cd_prioridade_entrega,
		ds_prioridade_entrega,
		coalesce(qt_dias_min_entrega,0) qt_dias_min_entrega,
		nr_seq_motivo_urgente
	from	regra_envio_prioridade_int
	where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	coalesce(nr_seq_motivo_urgente,nr_seq_motivo_urgente_w) = nr_seq_motivo_urgente_w
	and	(cd_material IS NOT NULL AND cd_material::text <> '')
	and 	cd_material = cd_material_w
	order by  nr_seq_motivo_urgente asc,
		qt_dias_min_entrega asc;

vet01	c01%RowType;


BEGIN

select	max(cd_estabelecimento),
	coalesce(max(nr_seq_motivo_urgente),0)
into STRICT	cd_estabelecimento_w,
	nr_seq_motivo_urgente_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

select 	min(a.dt_solic_item),
	min(cd_material)
into STRICT	dt_solic_item_w,
	cd_material_w
from	solic_compra_item a
where	a.nr_solic_compra = nr_solic_compra_p;

select	max(cd_grupo_material),
	max(cd_subgrupo_material),
	max(cd_classe_material)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_w;

select	count(1)
into STRICT	qt_regra_grupo_w
from	regra_envio_prioridade_int
where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and	cd_grupo_material = cd_grupo_material_w;

select	count(1)
into STRICT	qt_subgrupo_material_w
from	regra_envio_prioridade_int
where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and	cd_subgrupo_material = cd_subgrupo_material_w;

select	count(1)
into STRICT	qt_classe_material_w
from	regra_envio_prioridade_int
where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and	cd_classe_material = cd_classe_material_w;

select	count(1)
into STRICT	qt_cd_material_w
from	regra_envio_prioridade_int
where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and	cd_material = cd_material_w;

select	count(*)
into STRICT	qt_registros_w
from	regra_envio_prioridade_int
where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;

if (qt_registros_w = 0) then
	ds_dias_solic_w := 'WPD';
else

	select	max(CASE WHEN obter_dias_entre_datas(trunc(coalesce(c.dt_autorizacao, clock_timestamp())), trunc(dt_solic_item_w))=0 THEN  'EMERGENCIA' WHEN obter_dias_entre_datas(trunc(coalesce(c.dt_autorizacao, clock_timestamp())), trunc(dt_solic_item_w))=1 THEN 'EMERGENCIA' WHEN obter_dias_entre_datas(trunc(coalesce(c.dt_autorizacao, clock_timestamp())), trunc(dt_solic_item_w))=2 THEN 'URGENTE'  ELSE 'WPD' END )
	into STRICT	ds_dias_solic_w
	from	solic_compra_item a,
		solic_compra c
	where	a.nr_solic_compra = c.nr_solic_compra
	and	a.nr_solic_compra = nr_solic_compra_p;
end if;

if (ie_opcao_p = 'C') then
	select CASE WHEN ds_dias_solic_w='EMERGENCIA' THEN '1002' WHEN ds_dias_solic_w='URGENTE' THEN '1001' WHEN ds_dias_solic_w='WPD' THEN '0001' END
	into STRICT	ds_dias_solic_w
	;
elsif (ie_opcao_p = 'D') then
	ds_dias_solic_w := ds_dias_solic_w;
end if;

open c01;
loop
fetch c01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	begin
	select	max(obter_dias_entre_datas(trunc(c.dt_autorizacao), trunc(dt_solic_item_w)))
	into STRICT	qt_dias_solic_w
	from	solic_compra_item a,
		solic_compra c
	where	a.nr_solic_compra = c.nr_solic_compra
	and	a.nr_solic_compra = nr_solic_compra_p;

	exception
	when others then
		exit;
	end;

	if (qt_dias_solic_w <= vet01.qt_dias_min_entrega) then

		if (ie_opcao_p = 'C') then
			begin
			ds_dias_solic_w := vet01.cd_prioridade_entrega;
			exit;
			end;
		elsif (ie_opcao_p = 'D') then
			begin
			ds_dias_solic_w := vet01.ds_prioridade_entrega;
			exit;
			end;
		end if;

	end if;

	end;
end loop;
close c01;

return	ds_dias_solic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tag_categ_int_me (nr_solic_compra_p bigint, ie_opcao_p text) FROM PUBLIC;

