CREATE OR REPLACE FUNCTION obter_tamanho_panel ( cd_dto_p bigint, ie_tipo_obj_painel_p objeto_schematic.ie_tipo_obj_painel%type default null) RETURNS varchar AS $body$
DECLARE


nr_seq_panel_w		objeto_schematic.nr_sequencia%type;
ie_tamanho_panel_w	objeto_schematic.ie_tipo_obj_painel%type;


BEGIN
if (cd_dto_p IS NOT NULL AND cd_dto_p::text <> '') then

    nr_seq_panel_w := cd_dto_p;
    ie_tamanho_panel_w := ie_tipo_obj_painel_p;

    WHILE coalesce(ie_tamanho_panel_w::text, '') = '' LOOP
        select  nr_seq_obj_sup,
                ie_tipo_obj_painel
        into STRICT    nr_seq_panel_w,
                ie_tamanho_panel_w
        from    objeto_schematic
        where   nr_sequencia = nr_seq_panel_w;

        if (coalesce(nr_seq_panel_w::text, '') = '') then
            EXIT;
        end if;
    END LOOP;

end if;

RETURN	ie_tamanho_panel_w;

END;
$body$
