SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tasy_obter_cor ( nr_seq_cor_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_fundo_w		varchar(15);
ds_cor_fonte_w		varchar(15);
ds_cor_selecao_w	varchar(15);
resultado_w		varchar(15)	:= '';
ie_ocultar_w		varchar(15)	:= 'N';
ds_cor_html_w	varchar(15);

-- F - cor de fundo
--T - cor de fonte
--S - cor de seleção
-- H - Cor do HTML5
BEGIN

select	max(ds_cor_fundo),
	max(ds_cor_fonte),
	max(ds_cor_selecao),
	max(ie_ocultar),
	max(ds_cor_html)
into STRICT	ds_cor_fundo_w,
	ds_cor_fonte_w,
	ds_cor_selecao_w,
	ie_ocultar_w,
	ds_cor_html_w
from	tasy_padrao_cor_perfil
where	cd_estabelecimento	= cd_estabelecimento_p
and	nr_seq_padrao		= nr_seq_cor_p
and	cd_perfil		= cd_perfil_p;

if	(((coalesce(ds_cor_fundo_w::text, '') = '') and (coalesce(ie_opcao_p,'F') <> 'H')) or
    (( coalesce(ds_cor_html_w::text, '') = '' ) and (coalesce(ie_opcao_p,'F') = 'H'))) then
	select	max(ds_cor_fundo),
		max(ds_cor_fonte),
		max(ds_cor_selecao),
		max(ie_ocultar),
		max(ds_cor_html)
	into STRICT	ds_cor_fundo_w,
		ds_cor_fonte_w,
		ds_cor_selecao_w,
		ie_ocultar_w,
		ds_cor_html_w
	from	tasy_padrao_cor_perfil
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	nr_seq_padrao		= nr_seq_cor_p
	and	coalesce(cd_perfil::text, '') = '';
end if;

if	(((coalesce(ds_cor_fundo_w::text, '') = '') and (coalesce(ie_opcao_p,'F') <> 'H')) or
    (( coalesce(ds_cor_html_w::text, '') = '' ) and (coalesce(ie_opcao_p,'F') = 'H')))then
	select	max(ds_cor_fundo),
		max(ds_cor_fonte),
		max(ds_cor_selecao),
		max(ds_cor_html)
	into STRICT	ds_cor_fundo_w,
		ds_cor_fonte_w,
		ds_cor_selecao_w,
		ds_cor_html_w
	from	tasy_padrao_cor
	where	nr_sequencia		= nr_seq_cor_p;
end if;

if ( ie_opcao_p = 'F') then
	resultado_w := ds_cor_fundo_w;
elsif ( ie_opcao_p = 'T') then
	resultado_w := ds_cor_fonte_w;
elsif (ie_opcao_p = 'H') then
	resultado_w	:= ds_cor_html_w;
else
	resultado_w := ds_cor_selecao_w;
end if;

if (ie_ocultar_w = 'S') then
	resultado_w := null;
end if;

return resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
