CREATE OR REPLACE FUNCTION obter_taxa_ajuste (nr_seq_indice_p bigint, dt_vigencia_parcela_p timestamp) RETURNS bigint AS $body$
DECLARE


tx_ajuste_w		indice_reajuste_valor_fin.tx_ajuste%type;


BEGIN

  select max(tx_ajuste) tx_ajuste
  into STRICT tx_ajuste_w
  from indice_reajuste_valor_fin
  where nr_seq_indice = nr_seq_indice_p
  and dt_vigencia = (SELECT max(dt_vigencia)
                     from indice_reajuste_valor_fin
                     where dt_vigencia <= dt_vigencia_parcela_p
                     and nr_seq_indice = nr_seq_indice_p);

return tx_ajuste_w;

end;
$body$
LANGUAGE PLPGSQL
