SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_taxa_deprec_fiscal_bem ( nr_sequencia_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE



tx_fiscal_w			double precision;
tx_depreciacao_w			double precision;


BEGIN

select	coalesce(max(tx_fiscal),0)
into STRICT	tx_fiscal_w
from	pat_bem
where	nr_sequencia = nr_sequencia_p;

select	coalesce(max(a.pr_deprec_fiscal), tx_fiscal_w)
into STRICT	tx_depreciacao_w
from 	pat_bem_taxa a
where	a.nr_seq_bem = nr_sequencia_p
and	a.dt_vigencia = (
	SELECT	max(b.dt_vigencia)
	from	pat_bem_taxa b
	where	b.nr_seq_bem = nr_sequencia_p
	and	b.dt_vigencia <= dt_referencia_p);

return tx_depreciacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
