CREATE OR REPLACE FUNCTION obter_taxa_iss_nota ( nr_seq_nota_p bigint) RETURNS bigint AS $body$
DECLARE


tx_tributo_w		nota_fiscal_trib.tx_tributo%type;


BEGIN

tx_tributo_w := 0;

if nr_seq_nota_p > 0 then

	select	coalesce(sum(tx_tributo),0)
	into STRICT	tx_tributo_w
	from 	nota_fiscal_trib n,
		tributo t
	where	n.cd_tributo 	= t.cd_tributo
	and	t.ie_tipo_tributo	= 'ISS'
	and 	n.nr_sequencia	= nr_seq_nota_p;

end if;

return	tx_tributo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
