CREATE OR REPLACE FUNCTION obter_taxa_maior_porte ( nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


cd_convenio_w			bigint;
nr_porte_w			smallint;
nr_porte_maior_w			smallint;
cd_proc_maior_porte_w		bigint;
nr_atendimento_w			bigint;
nr_seq_proc_interno_w		bigint;

c01 CURSOR FOR
SELECT	b.cd_procedimento cd_procedimento,
		b.ie_origem_proced ie_origem_proced,
		a.nr_seq_proc_interno nr_seq_proc_interno
from	procedimento b,
		procedimento_paciente a
where	a.cd_procedimento 	= b.cd_procedimento
and	a.ie_origem_proced	= b.ie_origem_proced
and	b.ie_apuracao_custo	= 'M'
and	a.nr_cirurgia		= nr_cirurgia_p
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

Vet1		C01%Rowtype;


BEGIN

/*convenio da cirurgia*/

select	coalesce(max(nr_atendimento),0)
into STRICT	nr_atendimento_w
from	cirurgia
where	nr_cirurgia = nr_cirurgia_p;

/* Convenio do atendimento */

select	obter_convenio_atendimento(nr_atendimento_w)
into STRICT	cd_convenio_w
;

nr_porte_w	:= 0;
nr_porte_maior_w	:= 0;

open c01;
loop
fetch c01 into
	Vet1;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (coalesce(Vet1.nr_seq_proc_interno,0) > 0 ) then
		select	coalesce(max(nr_porte_anestesico),0)
		into STRICT	nr_porte_w
		from	convenio_taxa_cirurgia
		where	cd_convenio	= cd_convenio_w
		and		nr_seq_proc_interno	= Vet1.nr_seq_proc_interno;
	else
		select	coalesce(max(nr_porte_anestesico),0)
		into STRICT	nr_porte_w
		from	convenio_taxa_cirurgia
		where	cd_convenio	= cd_convenio_w
		and	cd_taxa_cirurgia	= Vet1.cd_procedimento;
	end if;

	if (nr_porte_w >= nr_porte_maior_w) then
		cd_proc_maior_porte_w	:= Vet1.cd_procedimento;
		nr_porte_maior_w		:= nr_porte_w;
	end if;

	end;
end loop;
close c01;

return cd_proc_maior_porte_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
