CREATE OR REPLACE FUNCTION obter_taxa_propaci ( nr_seq_propaci_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao
'T' - Tx procedimento
*/
vl_retorno_w		double precision;
tx_procedimento_w	varchar(80);


BEGIN

select	max(tx_procedimento)
into STRICT	tx_procedimento_w
from	procedimento_paciente
where	nr_sequencia	= nr_seq_propaci_p;

if (ie_opcao_p = 'T') then
	vl_retorno_w	:= tx_procedimento_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
