CREATE OR REPLACE FUNCTION obter_telefone_pf_pj_tit_rec (cd_pessoa_fisica_p text, cd_cgc_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
nr_ddd_celular_w		pessoa_fisica.nr_ddd_celular%type;
nr_telefone_celular_w	pessoa_fisica.nr_telefone_celular%type;
nr_ddi_celular_w		pessoa_fisica.nr_ddi_celular%type;	
 
 
/* ie_opcao_p 
Apenas terá influencia no retorno caso seja CGC. Para PF essa opcao n influencia 
1 - Telefone 
2 - Nome pessoa contato 
*/
							  
									  

BEGIN 
if (ie_opcao_p = 1) then 
 
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
		select	max(a.nr_ddd_celular), 
				max(a.nr_telefone_celular), 
				max(a.nr_ddi_celular) 
		into STRICT	nr_ddd_celular_w, 
				nr_telefone_celular_w, 
				nr_ddi_celular_w 
		from	pessoa_fisica a 
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
		 
		/*Aqui pega o telefone cfme ocorria antes no delphi, apenas concatenando com o DDD*/
 
		if (nr_telefone_celular_w IS NOT NULL AND nr_telefone_celular_w::text <> '') then 
		 
			ds_retorno_w := substr(nr_ddi_celular_w ||' (' || nr_ddd_celular_w ||') ' || nr_telefone_celular_w,1,254);
			 
		else /*Se n tiver telefone nesse cadastro, tratar para buscar do complemento da PF, cfme faz hj na visao do java*/
 
			 
			select	substr(obter_dados_pf_pj(cd_pessoa_fisica_p, null,'DIT') || 
					'('|| obter_dados_pf_pj(cd_pessoa_fisica_p, null,'DDT') || ') ' || 
					obter_dados_pf_pj(cd_pessoa_fisica_p, null,'T'),1,254) 
			into STRICT	ds_retorno_w 
			;
			 
		end if;
		 
	elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then 
 
		select 	substr( max(a.nr_ddi_telefone) || 
				 ' ('||max(a.nr_ddd_telefone)||') ' || 
						max(a.nr_telefone),1,254) nr_telefone 
		into STRICT	ds_retorno_w			 
		from 	pessoa_juridica a, 
				pessoa_juridica_estab b 
		where 	a.cd_cgc 	= b.cd_cgc 
		and   a. cd_cgc 	= cd_cgc_p 
		and   b.cd_estabelecimento = obter_estabelecimento_ativo;
 
	end if;
 
elsif (ie_opcao_p = 2) and (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then /*Essa opção é apenas para PJ*/
 
 
	select substr(b.nm_pessoa_contato,1,254) 	 
	into STRICT	ds_retorno_w	 
	from 	pessoa_juridica a, 
			pessoa_juridica_estab b 
	where 	a.cd_cgc = b.cd_cgc 
	and   a.cd_cgc = cd_cgc_p 
	and   b.cd_estabelecimento = obter_estabelecimento_ativo;
 
end if;	
 
return	substr(ds_retorno_w,1,254);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_telefone_pf_pj_tit_rec (cd_pessoa_fisica_p text, cd_cgc_p text, ie_opcao_p bigint) FROM PUBLIC;

