CREATE OR REPLACE FUNCTION obter_telefone_wsuite_usuario ( DS_LOGIN_P text ) RETURNS varchar AS $body$
DECLARE


ds_telefone_w		varchar(255);


BEGIN

  select MAX(CASE WHEN coalesce(NR_DDD_CELULAR::text, '') = '' THEN ''  ELSE '('||NR_DDD_CELULAR||') ' END ) || MAX(NR_TELEFONE_CELULAR)
  INTO STRICT	ds_telefone_w
  from WSUITE_SOLIC_INCLUSAO_PF
  where NR_SEQUENCIA = (SELECT NR_SEQ_INCLUSAO_PF from WSUITE_USUARIO where DS_LOGIN = DS_LOGIN_P);

  RETURN ds_telefone_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
