CREATE OR REPLACE FUNCTION obter_tempo_aguard_ag_exame (nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


dt_atendimento_w	timestamp;
dt_chegada_w		timestamp;
qt_retorno_w		bigint;



BEGIN

select	max(dt_atendimento),
	max(dt_chegada)
into STRICT	dt_atendimento_w,
	dt_chegada_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

if (dt_atendimento_w IS NOT NULL AND dt_atendimento_w::text <> '') and (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') then
	qt_retorno_w := round(((dt_atendimento_w - dt_chegada_w) * 1440),0);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
