CREATE OR REPLACE FUNCTION obter_tempo_aplicacao_medic ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1000);
qt_min_aplicacao_w	smallint  := 0;
qt_hora_aplicacao_w	smallint  := 0;


BEGIN

select	b.qt_min_aplicacao,
	b.qt_hora_aplicacao
into STRICT	qt_min_aplicacao_w,
	qt_hora_aplicacao_w
from 	prescr_material b,
	prescr_medica a
where 	a.nr_prescricao	= b.nr_prescricao
and	b.nr_sequencia	= nr_sequencia_p
and	a.nr_prescricao	= nr_prescricao_p
and	b.ie_agrupador	in (1, 5, 8, 12, 14);

if (qt_min_aplicacao_w IS NOT NULL AND qt_min_aplicacao_w::text <> '') and (qt_hora_aplicacao_w IS NOT NULL AND qt_hora_aplicacao_w::text <> '') and
	((coalesce(qt_min_aplicacao_w,0) > 0) and (coalesce(qt_hora_aplicacao_w,0) > 0))then
	ds_retorno_w	:=  ' ' || wheb_mensagem_pck.get_texto(306840) || ' ' || to_char(qt_hora_aplicacao_w) || ' ' || wheb_mensagem_pck.get_texto(306879) || ' ' || wheb_mensagem_pck.get_texto(306894) || ' '|| to_char(qt_min_aplicacao_w) || ' ' || lower(wheb_mensagem_pck.get_texto(306874)); -- em	-- h. e min.
elsif (coalesce(qt_min_aplicacao_w::text, '') = '') and (qt_hora_aplicacao_w IS NOT NULL AND qt_hora_aplicacao_w::text <> '') then
	ds_retorno_w	:= ' ' || wheb_mensagem_pck.get_texto(306840) || ' ' || to_char(qt_hora_aplicacao_w) || ' ' || wheb_mensagem_pck.get_texto(306879); -- em	--h.
elsif (qt_min_aplicacao_w IS NOT NULL AND qt_min_aplicacao_w::text <> '') and (coalesce(qt_hora_aplicacao_w::text, '') = '') then
	ds_retorno_w	:= ' ' || wheb_mensagem_pck.get_texto(306840) || ' ' || to_char(qt_min_aplicacao_w) || ' ' || lower(wheb_mensagem_pck.get_texto(306874)); -- em	--min.
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
