CREATE OR REPLACE FUNCTION obter_tempo_aplic_sol ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_tempo_aplicacao_w	double precision;

BEGIN

select	max(qt_tempo_aplicacao)
into STRICT	qt_tempo_aplicacao_w
from	prescr_solucao
where	nr_prescricao = nr_prescricao_p
and	nr_seq_solucao = nr_seq_solucao_p;

if (ie_opcao_p = 'H') then
	return	trunc(qt_tempo_aplicacao_w);
elsif (ie_opcao_p = 'M') then
	return	mod(qt_tempo_aplicacao_w,1) * 60;
end if;

end;
$body$
