CREATE OR REPLACE FUNCTION obter_tempo_atend_ag_exame (nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


dt_atendimento_w	timestamp;
dt_executada_w		timestamp;
ie_status_agenda_w	varchar(3);
qt_retorno_w		bigint;



BEGIN

select	max(dt_atendimento),
	max(dt_executada),
	max(ie_status_agenda)
into STRICT	dt_atendimento_w,
	dt_executada_w,
	ie_status_agenda_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

if (ie_status_agenda_w = 'E') and (dt_atendimento_w IS NOT NULL AND dt_atendimento_w::text <> '') and (dt_executada_w IS NOT NULL AND dt_executada_w::text <> '') then
	qt_retorno_w := round(((dt_executada_w - dt_atendimento_w) * 1440),0);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
