CREATE OR REPLACE FUNCTION obter_tempo_atend_local_pa (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select 	MAX(substr(obter_dif_data(dt_entrada_local,coalesce(dt_saida_local,clock_timestamp()),null),1,255))
into STRICT	ds_retorno_w
from   	historico_localizacao_pa a
where  	a.nr_atendimento = nr_atendimento_p
and    	a.dt_entrada_local = (SELECT max(b.dt_entrada_local) from historico_localizacao_pa b where a.nr_atendimento = b.nr_atendimento);


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
