CREATE OR REPLACE FUNCTION obter_tempo_atend_senhas_an (nr_seq_pac_senha_fila_p bigint, nr_seq_mov_senha_fila_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
 Parametro ie_opcao_p HH para retornar hh:mm:ss
		MM para retornar apenas o total de minutos
*/
dt_inicial_w	timestamp;
dt_final_w	timestamp;
qt_retorno_w	varchar(100) := '0';
qt_minuto_w 	bigint;

C01 CURSOR FOR
	SELECT	dt_inicio_atendimento,
		coalesce(dt_fim_atendimento,clock_timestamp()),
		round((coalesce(dt_fim_atendimento,clock_timestamp()) - dt_inicio_atendimento) * 1440)
	from 	atendimentos_senha a,
		movimentacao_senha_fila b
	where 	a.nr_seq_mov_senha_fila = b.nr_sequencia
	and 	a.nr_seq_pac_senha_fila = nr_seq_pac_senha_fila_p
	and 	b.nr_sequencia = nr_seq_mov_senha_fila_p
	order by b.nr_sequencia, a.nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	dt_inicial_w,
	dt_final_w,
	qt_minuto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

if (ie_opcao_p = 'HH') then
	begin
	qt_retorno_w := obter_dif_data(dt_inicial_w,dt_final_w,'');
	end;
end if;

if (ie_opcao_p = 'MM') then
	begin
	qt_retorno_w := qt_minuto_w;
	end;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
