CREATE OR REPLACE FUNCTION obter_tempo_ativ_real ( nr_seq_ordem_p bigint, nm_usuario_exec_p text) RETURNS bigint AS $body$
DECLARE


qt_tempo_ativ_w		bigint;


BEGIN

select	sum(Obter_tempo_entre_datas(dt_atividade,coalesce(dt_fim_atividade,clock_timestamp()),'M'))
into STRICT	qt_tempo_ativ_w
from	man_ordem_serv_ativ
where	nr_seq_ordem_serv	= nr_seq_ordem_p
and	nm_usuario_exec		= nm_usuario_exec_p
and	dt_atividade between trunc(clock_timestamp()) and fim_dia(clock_timestamp());

return qt_tempo_ativ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
