SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tempo_atraso_cir ( nr_cirurgia_p bigint, nr_seq_evento_p bigint) RETURNS bigint AS $body$
DECLARE


dt_inicio_prevista_w	timestamp;
dt_registro_w		timestamp;
qt_min_retorno_w	bigint;


BEGIN

if (nr_cirurgia_p > 0) and (nr_seq_evento_p > 0) then

	select	max(dt_registro)
	into STRICT	dt_registro_w
	from	evento_cirurgia_paciente
	where	nr_cirurgia	=	nr_cirurgia_p
	and	nr_seq_evento	=	nr_seq_evento_p
	and	coalesce(ie_situacao,'A') = 'A';

	if (dt_registro_w IS NOT NULL AND dt_registro_w::text <> '') then
		select	dt_inicio_prevista
		into STRICT	dt_inicio_prevista_w
		from	cirurgia
		where	nr_cirurgia	=	nr_cirurgia_p;

		qt_min_retorno_w	:= ROUND((dt_registro_w - (dt_inicio_prevista_w + 1/86400)) * 1440,1);

		if (qt_min_retorno_w < 0) then
			qt_min_retorno_w := 0;
		end if;
	end if;
end if;

return	qt_min_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
