CREATE OR REPLACE FUNCTION obter_tempo_atraso_pepo ( nr_cirurgia_p bigint, nr_seq_evento_dif_p bigint) RETURNS bigint AS $body$
DECLARE


ie_tempo_atraso_w	bigint;
qt_tempo_limite_w	bigint;
nr_seq_evento_w		bigint;
dt_registro_inic_w	timestamp;
dt_registro_final_w	timestamp;


BEGIN

select	max(qt_tempo_limite),
	max(nr_seq_evento)
into STRICT 	qt_tempo_limite_w,
	nr_seq_evento_w
from	tempo_cirurgico
where	nr_seq_evento_dif = nr_seq_evento_dif_p
and		coalesce(ie_situacao,'A') = 'A';

if (coalesce(qt_tempo_limite_w,0) > 0) then

	select	max(dt_registro)
	into STRICT 	dt_registro_inic_w
	from   	evento_cirurgia_paciente
	where	((nr_cirurgia   = nr_cirurgia_p) or (nr_seq_pepo = nr_cirurgia_p))
	and	nr_seq_evento = nr_seq_evento_w
	and	coalesce(ie_situacao,'A') = 'A';

	select	max(dt_registro)
	into STRICT 	dt_registro_final_w
	from   	evento_cirurgia_paciente
	where	((nr_cirurgia   = nr_cirurgia_p) or (nr_seq_pepo = nr_cirurgia_p))
	and	nr_seq_evento = nr_seq_evento_dif_p
	and	coalesce(ie_situacao,'A') = 'A';

	ie_tempo_atraso_w := Obter_Min_Entre_Datas(dt_registro_inic_w,dt_registro_final_w,1) - qt_tempo_limite_w;

end if;


return	ie_tempo_atraso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
