CREATE OR REPLACE FUNCTION obter_tempo_cirurgia ( nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_min_cirurgia_w			double precision;


BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) then
	select coalesce(max(nr_min_duracao_real),0)
	into STRICT	qt_min_cirurgia_w
	from	cirurgia
	where	nr_cirurgia		= nr_cirurgia_p;
end if;

RETURN qt_min_cirurgia_w;

END;
