CREATE OR REPLACE FUNCTION obter_tempo_decorrido (dt_evento_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
qt_diferenca_w	double precision;


BEGIN

qt_diferenca_w := clock_timestamp() - dt_evento_p;

if (qt_diferenca_w > 5) then
	ds_retorno_w := to_char(dt_evento_p,'dd/mm/yyyy');
elsif (qt_diferenca_w > 1) then
	ds_retorno_w := ceil(qt_diferenca_w) || ' ' + obter_desc_expressao(327048);--' days ago';
elsif	(qt_diferenca_w > (1 / 24)) then
	ds_retorno_w := ceil(qt_diferenca_w * 24) || ' ' + obter_desc_expressao(786403);
elsif	(qt_diferenca_w > (0.25 / 24)) then
	ds_retorno_w := ceil(qt_diferenca_w * 1440) || ' ' + obter_desc_expressao(786407);
else
	ds_retorno_w := obter_desc_expressao(786411);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
