CREATE OR REPLACE FUNCTION obter_tempo_duracao_md ( qt_tempo_p text, nr_default_p bigint ) RETURNS bigint AS $body$
BEGIN
  if (position(':' in qt_tempo_p) > 0) then	
    return dividir_md(coalesce(obter_minutos_hora_md(qt_tempo_p), 0), 60);
  else
    return coalesce(qt_tempo_p, nr_default_p);
  end if;
end;
