CREATE OR REPLACE FUNCTION obter_tempo_duracao_proced ( cd_agenda_p bigint, cd_profissional_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_pessoa_fisica_p text, nr_seq_proc_interno_p bigint, ie_lado_procedimento_p text default null, cd_convenio_p bigint default null, cd_categoria_p text default null, cd_plano_p text default null, nr_seq_agenda_p bigint default null, ie_dia_semana_p bigint default null) RETURNS bigint AS $body$
DECLARE


nr_min_duracao_w	bigint := 0;
nr_seq_tempo_w		bigint;
qt_anos_w		integer;
cd_estab_agenda_w	smallint;
dt_agenda_w		timestamp;
ie_dia_semana_w		smallint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	tempo_procedimento
	where	((cd_procedimento = cd_procedimento_p) or (coalesce(cd_procedimento::text, '') = ''))
	and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_p) or (coalesce(ie_origem_proced::text, '') = '')))
	and	((nr_seq_proc_interno = nr_seq_proc_interno_p) or (coalesce(nr_seq_proc_interno::text, '') = ''))
	--and	nvl(cd_agenda, nvl(cd_agenda_p,0)) 			= nvl(cd_agenda_p,0)
	and	((cd_agenda = cd_agenda_p) or (coalesce(cd_agenda::text, '') = ''))
	and	coalesce(cd_pessoa_fisica, coalesce(cd_profissional_p,'0'))	= coalesce(cd_profissional_p,'0')
	and	coalesce(ie_lado,coalesce(ie_lado_procedimento_p,'0')) 		= coalesce(ie_lado_procedimento_p,'0')
	and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
	and	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and	((cd_plano = cd_plano_p) or (coalesce(cd_plano::text, '') = ''))
	and	qt_anos_w  between coalesce(qt_idade_minima,0) and coalesce(qt_idade_maxima,999)
	and	((cd_estabelecimento 	= cd_estab_agenda_w) or (coalesce(cd_estabelecimento::text, '') = ''))
	and	coalesce(qt_minuto,0) > 0
	and	((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_semana_w not in (1,7))) or (coalesce(ie_dia_semana::text, '') = ''))
	order by coalesce(nr_seq_proc_interno,0),
		 coalesce(cd_convenio,0),
		 coalesce(cd_categoria,0),
		 coalesce(cd_plano,0),
		 coalesce(cd_pessoa_fisica,'0'),
		 coalesce(cd_agenda,0),
		 coalesce(cd_estabelecimento,0);


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (coalesce(ie_dia_semana_p::text, '') = '') then

	select	max(hr_inicio)
	into STRICT	dt_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;
	
	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
		ie_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_w);
	else
		ie_dia_semana_w	:= null;
	end if;
	
else

	ie_dia_semana_w	:= ie_dia_semana_p;
	
end if;

if	(cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '' AND ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') or (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	
	begin
	select	(obter_idade(dt_nascimento, clock_timestamp(), 'A'))::numeric
	into STRICT	qt_anos_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	exception
	when others then
		qt_anos_w := 0;
	end;
	
	select	max(cd_estabelecimento)
	into STRICT	cd_estab_agenda_w
	from	agenda
	where	cd_agenda = cd_agenda_p;

	open C01;
	loop
	fetch C01 into	
		nr_seq_tempo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_tempo_w := nr_seq_tempo_w;
		end;
	end loop;
	close C01;

	if (nr_seq_tempo_w > 0) then
	
		select	coalesce(max(qt_minuto),0)
		into STRICT	nr_min_duracao_w
		from	tempo_procedimento
		where	nr_sequencia = nr_seq_tempo_w;
		
	end if;	
end if;

return nr_min_duracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tempo_duracao_proced ( cd_agenda_p bigint, cd_profissional_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_pessoa_fisica_p text, nr_seq_proc_interno_p bigint, ie_lado_procedimento_p text default null, cd_convenio_p bigint default null, cd_categoria_p text default null, cd_plano_p text default null, nr_seq_agenda_p bigint default null, ie_dia_semana_p bigint default null) FROM PUBLIC;

